INSERT INTO `CubeCart_email_template` VALUES(1, '1', 'Default Emails', '<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd">\r\n<html>\r\n<head>\r\n<meta http-equiv="Content-Type" content="text/html; charset=UTF-8" />\r\n<style type="text/css">\r\nhtml, body, table {\r\n	font-family: Arial;\r\n	font-size: 14px;\r\n}\r\n</style>\r\n<title>Default HTML Template</title>\r\n<base href="{$DATA.storeURL}" />\r\n</head>\r\n<body bgcolor="#f7f7f7">\r\n<table border="0" cellpadding="0" cellspacing="0" width="100%">\r\n  <tbody>\r\n    <tr>\r\n      <td align="center"><table bgcolor="#ffffff" border="0" cellpadding="15" cellspacing="0" width="580">\r\n          <tbody>\r\n            <tr>\r\n              <td><a href="{$DATA.storeURL}"><img alt="{$DATA.storeName}" border="0" src="{$DATA.logoURL}" /></a></td>\r\n            </tr>\r\n            <tr>\r\n              <td> {$EMAIL_CONTENT}</td>\r\n            </tr>\r\n            <tr>\r\n              <td><p> Kind regards,</p>\r\n                <p> The {$DATA.storeName} Staff<br />\r\n                  <a href="{$DATA.storeURL}">{$DATA.storeURL}</a></p></td>\r\n            </tr>\r\n          </tbody>\r\n        </table></td>\r\n    </tr>\r\n  </tbody>\r\n</table>\r\n</body>\r\n</html>', '{$EMAIL_CONTENT}\r\n\r\nKind regards,\r\n\r\nThe {$DATA.storeName} Staff\r\n{$DATA.storeURL}'); #EOQ
INSERT INTO `CubeCart_email_template` VALUES(2, '0', 'Default Newsletter', '<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd">\r\n<html>\r\n<head>\r\n<meta http-equiv="Content-Type" content="text/html; charset=UTF-8" />\r\n<style type="text/css">\r\nhtml, body, table {\r\n	font-family: Arial;\r\n	font-size: 14px;\r\n}\r\n</style>\r\n<title>Default HTML Newsletter Template</title>\r\n<base href="{$DATA.storeURL}" />\r\n</head>\r\n<body bgcolor="#f7f7f7">\r\n<table border="0" cellpadding="0" cellspacing="0" width="100%">\r\n  <tbody>\r\n    <tr>\r\n      <td align="center"><table bgcolor="#ffffff" border="0" cellpadding="15" cellspacing="0" width="580">\r\n          <tbody>\r\n            <tr>\r\n              <td><a href="{$DATA.storeURL}"><img src="{$DATA.logoURL}" alt="{$DATA.storeName}" border="0" /></a></td>\r\n            </tr>\r\n            <tr>\r\n              <td>{$EMAIL_CONTENT}</td>\r\n            </tr>\r\n            <tr>\r\n              <td><p>Kind regards,</p>\r\n                <p>The {$DATA.storeName} Staff<br />\r\n                  <a href="{$DATA.storeURL}">{$DATA.storeURL}</a></p>\r\n                <hr size="1" />\r\n                <p><font size="2">To unsubscribe please follow the link below:<br />\r\n                  <a href="{$DATA.unsubscribeURL}">{$DATA.unsubscribeURL}</a></font></p></td>\r\n            </tr>\r\n          </tbody>\r\n        </table></td>\r\n    </tr>\r\n  </tbody>\r\n</table>\r\n</body>\r\n</html>', '{$EMAIL_CONTENT}\r\n\r\nKind regards,\r\n\r\nThe {$DATA.storeName} Staff\r\n{$DATA.storeURL}\r\n\r\n-------------------\r\n\r\nTo unsubscribe please follow the link below:\r\n{$DATA.unsubscribeURL}'); #EOQ